const MedicalBookingScreen = ({ onNavigate }) => {
  const [step, setStep] = useState(0);
  const [booking, setBooking] = useState({ emergencyType:null, patient:{ name:'', age:'', gender:'', contact:'' }, vehicleType:null, hospitalPreference:null, location:'' });
